INSERT INTO "public"."frequently_asked_questions" ("id", "question", "answer", "user_id", "created_at", "updated_at",
                                                   "deleted_at")
VALUES (1, 'В чем преимущество ИОТ?',
        '<p>ИОТ позволяет самостоятельно конструировать собственный уникальный маршрут обучения в университете. Система ИОТ ориентирована на индивидуализацию студента в учебном пространстве как по уровню сложности, так и по выбору дисциплин, что позволяет ему углубленно изучить выбранную траекторию и стать конкурентоспособнее на рынке труда.</p>',
        2, '2023-03-24 23:15:38', '2023-03-24 23:15:38', NULL),
       (2, 'Как я с этим справлюсь?',
        '<p>Навигатором в мире ИОТ для первокурсника станет тьютор, который профессионально поможет ориентироваться в образовательном пространстве, в мире новых локаций, понятий и правил. Определить первоочередные задачи и спрогнозировать возможность их достижения с помощью образовательных предложений, которые делает университет.</p><p>Главными руководителями в мире профессиональных и научно-исследовательских достижений станут преподаватели, а также руководители образовательных программ.</p>',
        2, '2023-03-24 23:16:02', '2023-03-24 23:16:02', NULL),
       (3, 'С кем я буду учиться?',
        '<p>Каждый семестр, на основе выбора студента (форма обучения, уровень сложности) формируются учебные команды. Здесь студент-айтишник может встретиться со студентами радиотехнических и инженерных направлений. За 4 года в общеуниверситетском пространстве каждый обучающийся становится участником минимум 20 смешанных учебных команд. При этом, понятие академической группы сохраняется на некоторых дисциплинах, например, «Введение в специальность».</p>',
        2, '2023-03-24 23:16:26', '2023-03-24 23:16:26', NULL),
       (4, 'Что выбрать: онлайн или традиционный курс?',
        '<p>Традиционная форма предусматривает непосредственное присутствие на лекциях, семинарах и прямой контакт с преподавателями.</p><p>Выбор онлайн-курса позволяет студенту самостоятельно распределять время освоения учебного материала, но при этом существуют контрольные точки и дедлайны. При возникновении вопросов по курсу, студент всегда может обратиться за помощью в онлайн-форме или проконсультироваться с преподавателями университета.</p><p>Онлайн-курсы не уступают традиционным, так как являются полностью сертифицированными, и соответствуют современным образовательным стандартам, содержат актуальные знания.</p>',
        2, '2023-03-24 23:16:46', '2023-03-24 23:16:46', NULL),
       (5, 'Кем я стану, когда получу диплом?',
        '<p>Университет гарантирует каждому выпускнику-бакалавру базовую профессиональную подготовку по направлению, которое он сам выберет при поступлении или в процессе обучения.Диплом бакалавра дает возможности для начала карьеры, а также позволяет продолжить обучение в магистратуре.</p><p>Выбор индивидуальных образовательных траекторий дает возможность получать именно те знания и компетенции, которые максимально раскроют потенциал студента и позволят успешно выйти на современный рынок труда. Система ИОТ способствует формированию универсальных компетенций, которые необходимы каждому:</p><ul><li>опыт проектной деятельности;</li><li>опыт работы в команде;</li><li>навыки межкультурного взаимодействия;</li><li>цифровая культура;</li><li>критическое и креативное мышление;</li><li>способность к продуктивному действию в условиях неопределенности и дефицита данных.</li></ul>',
        2, '2023-03-24 23:17:07', '2023-03-24 23:17:32', NULL),
       (6, 'Если я выбрал обучение на онлайн-курсе, у меня будет возможность общаться с преподавателями?',
        '<p>Общение с преподавателями предусмотрено в чате на платформе «Открытое образование», на которой реализуются онлайн-курсы. Кроме того, можно подойти с вопросом к преподавателям УрФУ, которые помогут прояснить непонятные моменты.</p>',
        2, '2023-03-24 23:17:54', '2023-03-24 23:17:54', NULL),
       (7, 'Если я не сдам экзамен по онлайн курсу, будет ли возможность пересдать?',
        '<p>Как и для всех курсов, для онлайн-курсов предусмотрены две пересдачи.</p>', 2, '2023-03-24 23:18:14',
        '2023-03-24 23:18:14', NULL);

