INSERT INTO "public"."professional_trajectories" ("id", "title", "description", "color", "abbreviated_name", "user_id", "created_at", "updated_at", "deleted_at") VALUES
(1, 'Data-science', '<p><strong style="color: rgb(32, 33, 36);">Data Science</strong><span style="color: rgb(32, 33, 36);">&nbsp;– это использование научных методов в работе с большими данными для того, чтобы найти нужное решение.&nbsp;</span><strong style="color: rgb(32, 33, 36);">Data Scientist</strong><span style="color: rgb(32, 33, 36);">&nbsp;работает с данными так же, как учёный любой сферы знания. Он применяет математическую статистику, логические принципы и современные инструменты визуализации, чтобы получить результат.</span></p>', '#3547f4', 'data', 1, '2023-03-19 13:57:50', '2023-03-19 14:41:04', NULL),
(2, 'Web-разработка', '<p><strong style="color: rgb(32, 33, 36);">WEB</strong><span style="color: rgb(32, 33, 36);">-</span><strong style="color: rgb(32, 33, 36);">разработка</strong><span style="color: rgb(32, 33, 36);">&nbsp;— процедура создания&nbsp;</span><strong style="color: rgb(32, 33, 36);">WEB</strong><span style="color: rgb(32, 33, 36);">-приложения или&nbsp;</span><strong style="color: rgb(32, 33, 36);">WEB</strong><span style="color: rgb(32, 33, 36);">-сайта. Основными этапами этого процесса являются такие мероприятия, как&nbsp;</span><strong style="color: rgb(32, 33, 36);">WEB</strong><span style="color: rgb(32, 33, 36);">-дизайн, вёрстка страниц сайта,&nbsp;</span><strong style="color: rgb(32, 33, 36);">WEB</strong><span style="color: rgb(32, 33, 36);">-программирование на стороне сервера и клиента, а также работы по конфигурированию&nbsp;</span><strong style="color: rgb(32, 33, 36);">WEB</strong><span style="color: rgb(32, 33, 36);">-сервера.</span></p>', '#3689F5', 'web', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(3, 'Администрирование баз данных', '<p><strong style="color: rgb(0, 0, 0);">Администрирование данных</strong><span style="color: rgb(0, 0, 0);">&nbsp;— управление&nbsp;информационными&nbsp;ресурсами, включая планирование&nbsp;базы данных, разработку и внедрение&nbsp;стандартов, определение ограничений и процедур, а также концептуальное и логическое проектирование баз данных.</span></p>', '#FA541C', 'database', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(4, 'Mobile разработка', '<p><strong style="color: rgb(32, 33, 36);">Разработка</strong><span style="color: rgb(32, 33, 36);">&nbsp;приложений для&nbsp;</span><strong style="color: rgb(32, 33, 36);">мобильных</strong><span style="color: rgb(32, 33, 36);">&nbsp;устройств —&nbsp;</span><strong style="color: rgb(32, 33, 36);">это</strong><span style="color: rgb(32, 33, 36);">&nbsp;процесс, при котором приложения разрабатываются для небольших портативных устройств, таких, как КПК, смартфоны или сотовые телефоны.</span></p>', '#36C8F5', 'mobile', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(5, 'GameDev', '<p><strong style="color: rgb(32, 33, 36);">Геймдев</strong><span style="color: rgb(32, 33, 36);">&nbsp;(</span><strong style="color: rgb(32, 33, 36);">GameDev</strong><span style="color: rgb(32, 33, 36);">, от английского games development — «разработка игр») — процесс создания игры: от разработки и дизайна до выпуска на рынок.&nbsp;</span><strong style="color: rgb(32, 33, 36);">Это</strong><span style="color: rgb(32, 33, 36);">&nbsp;могут быть игры для мобильных телефонов, консолей, компьютеров или других гаджетов.</span></p>', '#6236F5', 'gamedev', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(6, 'Desktop разработка', '<p><span style="color: rgb(32, 33, 36);">Десктопное приложение —&nbsp;</span><strong style="color: rgb(32, 33, 36);">программа, которая устанавливается на компьютер пользователя и работает под управлением операционной системы</strong><span style="color: rgb(32, 33, 36);">. Такие приложения высокопроизводительные, могут работать напрямую с принтерами, сканерами, факсами и прочей техникой.</span></p>', '#36F5E2', 'desktop', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(7, 'Управление проектами', '<p><span style="color: rgb(77, 81, 86);">Управление проектами — деятельность по решению задач и достижению поставленных целей проекта. Согласно PMBOK, управление проектами есть применение знаний, навыков, инструментов и техник при выполнении проектной деятельности для достижения требований проекта и запланированных результатов.</span></p>', '#A8F536', 'projects', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(8, 'Аналитика', '<p><strong style="color: rgb(17, 17, 17);"><em>В широком смысле IT-аналитик — это специалист собирающий и делающий выводы на основе данных</em></strong><span style="color: rgb(17, 17, 17);">. Рабочий процесс аналитика состоит из сбора, обработки и заключения. Результат работы помогает ответить на вопрос — чего хочет заказчик?</span></p>', '#36F562', 'analytics', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(9, 'DevOps', '<p><span style="color: rgb(77, 81, 86);">DevOps — методология активного взаимодействия специалистов по разработке со специалистами по информационно-технологическому обслуживанию и взаимная интеграция их рабочих процессов друг в друга для обеспечения качества продукта.</span></p>', '#FA8C16', 'devops', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(10, 'Интернет-маркетинг', '<p><span style="color: rgb(77, 81, 86);">Интернет-маркетинг — практика использования всех аспектов традиционного маркетинга в Интернете, с целью продажи продукта или услуги покупателям и управления взаимоотношениями с ними.</span></p>', '#36F5A2', 'marketing', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(11, 'Программирование 1с, ERP', '<p><strong style="color: rgb(32, 33, 36);">Программирование 1С</strong><span style="color: rgb(32, 33, 36);">&nbsp;направлено на то, чтобы максимально подстроить систему под особенности конкретного бизнеса. Это позволит: упростить работу персонала и сделать ее максимально удобной; проще и быстрее принимать управленческие решения.</span></p>', '#E236F5', '1c', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(12, 'Информационная безопасность', '<p><span style="color: rgb(77, 81, 86);">Информационная безопасность — практика предотвращения несанкционированного доступа, использования, раскрытия, искажения, изменения, исследования, записи или уничтожения информации. Это универсальное понятие применяется вне зависимости от формы, которую могут принимать данные.</span></p>', '#73D13D', 'security', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(13, 'Робототехника', '<p><span style="color: rgb(77, 81, 86);">Робототе́хника — прикладная наука, занимающаяся разработкой автоматизированных технических систем и являющаяся важнейшей технической основой развития производства.</span></p>', '#F53689', 'robots', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(14, 'Дизайн', '<p><span style="color: rgb(51, 51, 51);">Дизайнер&nbsp;— специалист, разрабатывающий визуальную часть ИТ-продукта. Реализовать творческие способности можно в&nbsp;трёх направлениях: графический дизайн, веб-дизайн и&nbsp;гейм-дизайн. Принимать дизайнера за&nbsp;свободную, эксцентричную личность, творящую только в&nbsp;паре с&nbsp;музой, ошибочно. Он&nbsp;воплощает в&nbsp;жизнь требования клиента, оформленные в&nbsp;виде чётких указаний (ТЗ), отступить от&nbsp;которых значит получить на&nbsp;выходе иной результат, нежели хотел заказчик.</span></p>', '#E8F536', 'ux/ui', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(15, 'Тестирование', '<p><strong style="color: rgb(0, 0, 0);">Тести́рование програ́ммного обеспе́че́ния</strong><span style="color: rgb(0, 0, 0);">&nbsp;— процесс исследования, испытания&nbsp;программного продукта, имеющий своей целью проверку соответствия между реальным поведением программы и её ожидаемым поведением на конечном наборе тестов, выбранных определённым образом</span></p>', '#A236F5', 'tests', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL),
(16, 'IOT', '<p><span style="color: rgb(77, 81, 86);">Интернет вещей — концепция сети передачи данных между физическими объектами, оснащёнными встроенными средствами и технологиями для взаимодействия друг с другом или с внешней средой.</span></p>', '#F53649', 'iot', NULL, '2023-03-19 13:57:50', '2023-03-19 13:57:50', NULL);
