INSERT INTO "professions" ("id", "title", "description", "headhunter_search_text", "vacancies_count", "area_vacancies_count", "maximal_salary", "minimal_salary", "median_salary", "photo_id", "user_id", "created_at", "updated_at", "deleted_at") VALUES
(1, 'Front-end разработчик', 'Нет описания!', 'Front-end OR JavaScript OR JS OR React', 3450, 256, 450000, 20000, 130000, 147, 1, '2023-02-18 20:51:51', '2023-04-10 00:03:11', NULL),
(2, 'Аналитик данных', 'Аналитик данных (или дата-аналитик) — это специалист, который собирает, обрабатывает, изучает и интерпретирует данные. Его работа помогает принимать решения в бизнесе, управлении и науке.', 'Data Analyst OR Analyst', 21176, 1423, 400000, 15000, 85000, 131, 1, '2023-02-22 21:07:27', '2023-04-10 00:02:47', NULL),
(3, 'Инженер данных', 'Инженер данных или Data Engineer — это специалист, который занимается построением и обслуживанием инфраструктуры для работы с данными, а также их предварительной обработкой. Инженер не участвует в анализе, но обеспечивает аналитиков нужными мощностями, инструментами и подготовленными данными.', 'Data Engineer', 223, 15, 400000, 30000, 195000, 189, 1, '2023-02-22 21:08:50', '2023-04-10 00:02:08', NULL),
(4, 'ML-инженер', 'Специалист по Machine Learning, или ML-инженер анализирует большие объёмы информации, создаёт модели для прогнозирования в бизнесе, медицине, промышленности. Задача ML-инженера — обучать нейросети, проектировать аналитические системы и рекомендательные сервисы на основе алгоритмов машинного обучения.', 'Machine Learning', 125, 12, 400000, 40000, 175000, 184, 1, '2023-02-22 21:10:02', '2023-04-10 00:00:51', NULL),
(5, 'Backend-разработчик', 'Бэк-энд разработчик — это специалист, который занимается программно-административной частью веб-приложения, внутренним содержанием системы, серверными технологиями — базой данных, архитектурой, программной логикой.', 'Back-end OR Backend OR Backend Developer OR Back-end Developer OR Backend разработчик OR бэкенд разработчик', 1691, 129, 600000, 25000, 150000, 174, 1, '2023-02-22 21:11:46', '2023-04-10 00:03:23', NULL),
(6, 'Fullstack-разработчик', 'Fullstack Developer — это разработчик, который принимает непосредственное участие во всех этапах разработки веб-приложений — от создания клиентской части (визуальная часть + пользовательская логика) до реализации серверной (базы данных, серверная архитектура, программная логика).', 'Fullstack Developer', 258, 19, 375000, 30000, 140000, 178, 1, '2023-02-22 21:13:14', '2023-04-10 00:03:51', NULL),
(7, 'Администратор баз данных', 'Администратор баз данных - специалист, отвечающий за выработку требований к базе данных, её проектирование, реализацию, эффективное использование и сопровождение, включая управление учётными записями пользователей БД и защиту от несанкционированного доступа.', 'Database OR Database Administrator OR DBA OR MySQL OR Oracle DB OR PostgreSQL', 3447, 310, 500000, 20000, 135000, 186, 1, '2023-02-22 21:16:24', '2023-04-10 00:01:23', NULL),
(8, 'Разработчик баз данных', 'Разработчик баз данных - это специалист, в деятельность которого входит создание баз данных, их укомплектование, модернизация и обслуживание.', 'Database Developer OR MySQL OR Oracle DB OR PostgreSQL', 3350, 301, 500000, 20000, 135000, 187, 1, '2023-02-22 21:17:46', '2023-04-10 00:01:38', NULL),
(9, 'Архитектор баз данных', 'Архитектор баз данных  — опытный технический специалист, который руководит целой группой сотрудников, обслуживающих систематизированный массив четко структурированной взаимосвязанной информации — базы данных, хранящейся в цифровом формате.', 'Database Architect', 8, 0, 250000, 199500, 224750, 182, 1, '2023-02-22 21:18:59', '2023-04-09 00:02:04', NULL),
(10, 'Мобильный разработчик', 'Разработчик мобильных приложений - это программист, который создает приложения (программы) для телефонов, планшетов и других мобильных устройств.', 'Mobile developer', 403, 19, 500000, 20000, 120000, 175, 1, '2023-02-22 21:20:25', '2023-04-10 00:03:27', NULL),
(11, 'Разработчик игр', 'Разработчики игр – программист, который при помощи движков создает различные игры.', 'Gamedev', 208, 6, 500000, 20000, 100000, 129, 1, '2023-02-22 21:23:14', '2023-04-10 00:02:49', NULL),
(12, 'Разработчик Windows приложений', 'Разработчик приложений для Windows - создает программы, приложения, поддерживаемые ОП Windows.', 'Desktop developer', 103, 6, 300000, 25000, 100000, 173, 1, '2023-02-22 21:27:06', '2023-04-10 00:02:51', NULL),
(13, 'Менеджер проектов', 'Менеджер проектов составляет план разработки сайта, организует команду и выстраивает весь процесс так, чтобы он соблюдался и был выполнен в срок', 'Project manager', 12400, 920, 600000, 20000, 100000, 185, 1, '2023-02-22 21:28:24', '2023-04-10 00:01:09', NULL),
(14, 'Системный аналитик', 'Системный аналитик помогает оптимизировать и автоматизировать работу компании и её подразделений. Этот специалист разбирается в менеджменте, экономике и информационных технологиях — помогает скоординировать процесс разработки ПО так, чтобы результат был максимально продуктивным.', 'Analyst OR System analyst', 21176, 1423, 400000, 15000, 85000, 177, 1, '2023-02-22 21:29:30', '2023-04-10 00:03:48', NULL),
(15, 'Бизнес-аналитик', 'Бизнес-аналитик помогает компании сформулировать потребности, разработать решения, которые будут полезны, и провести необходимые изменения. При этом он умеет правильно донести информацию всем заинтересованным лицам', 'Business analyst', 933, 101, 600000, 15000, 104200, 128, 1, '2023-02-22 21:31:49', '2023-04-10 00:04:16', NULL),
(16, 'Продуктовый аналитик', 'Продуктовый аналитик работает с цифрами, его главная задача — сделать так, чтобы продукт рос или вырастить его с нуля. Метрики, по которым это отслеживают, зависят от самого продукта, бизнеса и стратегии. Проще говоря, продуктовый аналитик собирает и изучает данные, которые могут быть полезны для развития продукта.', 'Product analyst', 350, 15, 400000, 30000, 120000, 172, 1, '2023-02-22 21:33:08', '2023-04-10 00:02:54', NULL),
(17, 'Технический писатель', 'Технический писатель - специалист, занимающийся документированием в рамках решения технических задач, в частности разработки программного обеспечения. Основная задача технического писателя — написание документа, который бы удовлетворял определённым требованиям.', 'Technical writer OR Technical author', 264, 27, 450000, 19500, 85000, 176, 1, '2023-02-22 21:34:23', '2023-04-10 00:03:29', NULL),
(18, 'DevOps инженер', 'DevOps инженер — это специалист, который синхронизирует этапы разработки программного продукта, знает, в чем заключается работа разработчиков, QA, менеджеров, и автоматизирует их задачи, умеет программировать и быстро изучает новые инструменты.', 'DevOps', 783, 84, 700000, 35000, 180000, 183, 1, '2023-02-22 21:35:28', '2023-04-10 00:00:50', NULL),
(19, 'Маркетолог', 'Маркетолог — это специалист по продвижению товаров и услуг на рынке. Это человек, который знает вкусы и предпочтения аудитории и умеет предложить именно то, чего сейчас требуют потенциальные покупатели.', 'Marketing', 18001, 960, 25000000, 15000, 82500, 136, 1, '2023-02-22 21:37:21', '2023-04-10 00:02:27', NULL),
(20, 'Программист 1C', 'Программисты 1С занимаются внедрением и сопровождением программ 1С в организациях: устанавливают и настраивают, дорабатывают и обновляют их, а ещё консультируют пользователей.', '1C OR 1C developer', 115097, 5643, 465000, 18000, 120000, 188, 1, '2023-02-22 21:38:53', '2023-04-10 00:02:06', NULL),
(21, 'Специалист по информационной безопасности', 'Специалист по информационной безопасности обеспечивает конфиденциальность данных, предотвращает утечку или несанкционированный доступ к информации, принимает непосредственное участие в создании системы защиты информации, ее аудите и мониторинге, анализирует информационные риски', 'Information Security Specialist', 983, 78, 350000, 17500, 60000, 163, 1, '2023-02-22 21:40:30', '2023-04-10 00:00:24', NULL),
(22, 'Инженер-робототехник', 'Инженер-робототехник – специалист, который разрабатывает архитектуру и вводит в эксплуатацию роботов, приборы и сложные робототехнические системы.', 'robots', 163, 8, 260000, 25000, 60000, 184, 1, '2023-02-22 21:43:01', '2023-04-10 00:04:18', NULL),
(23, 'UX/UI дизайнер', 'UX-дизайнер исследует ЦА, продумывает структуру продукта, анализирует пользовательские пути, а визуализирует его концепцию UI-специалист. Так как работа над UX- и UI-дизайном тесно связана между собой, чаще всего ею занимается один и тот же человек.', 'design OR ux/ui OR designer', 18388, 953, 325000, 15000, 65000, 180, 1, '2023-02-22 21:44:40', '2023-04-10 00:04:36', NULL),
(24, 'Продуктовый дизайнер', 'Продуктовый дизайнер формирует концепцию продукта, определяет, как с помощью дизайна решать проблемы бизнеса и достигать поставленных целей, и вместе с менеджером продукта ведет продуктовую команду к воплощению стратегии.', 'product designer OR design', 18205, 947, 325000, 15000, 65000, 179, 1, '2023-02-22 21:45:51', '2023-04-10 00:04:09', NULL),
(25, 'Тестировщик', 'Тестировщик — это специалист, который занимается проверкой работоспособности десктопных программ и мобильных приложений.', 'test OR QA engineers', 10414, 779, 750000, 15000, 75000, 181, 1, '2023-02-22 21:47:39', '2023-04-10 00:00:41', NULL),
(26, 'IoT-разработчик', 'iOT разработку еще называют m2m разработкой (Machine-to-Machine). Упрощенно, это система, в которой устройства обмениваются данными между собой без участия человека. Это могут быть системы охраны предприятия, системы «умного дома» или учетная система для логистической компании.', 'IoT OR IoT engineer', 245, 21, 400000, 30000, 90000, 146, 1, '2023-02-22 21:50:13', '2023-04-10 00:00:16', NULL);
