INSERT INTO "reviews" ("id", "author", "text", "educational_direction", "year_of_issue", "course", "hidden", "photo_id", "user_id", "created_at", "updated_at", "deleted_at") VALUES
(3,	'Алексей Фомин',	'На свою специальность решил поступить, так как она многогранна и интересна. Можно много где себя реализовать в будущем. Об Урфу был наслышан ещё в школе, мне показалось, что это достаточно престижный университет, и я считаю, что не ошибся с выбором.',	'Управление в технических системах',	NULL,	'2',	'f',	5,	3,	'2022-11-07 16:40:38',	'2022-11-07 16:40:38',	NULL),
(2,	'Алена Сенцова',	'Только в последние два года обучения в школе я поняла, кем хочу быть, и поставила цель стать программистом, поступить в ИРИТ-РТФ на направление «Программная инженерия», так как мои братья тоже программисты и советовали мне поступать именно сюда. Но несмотря на успешную сдачу ЕГЭ, я не смогла пройти по баллам туда, куда хотела. Была ли я расстроена? Конечно! Но у меня был запасной вариант – направление «Радиотехника», куда я подавала копии документов. В последний день я переложила оригиналы документов на мое направление, и вот я тут. Рассматривала ли я другие университеты для поступления? Да, но именно УрФУ представляет те направления подготовки, которые были мне ближе всего.',	'Радиотехника',	NULL,	'2',	'f',	4,	3,	'2022-11-07 16:39:13',	'2022-11-07 16:39:13',	NULL),
(4,	'Алексей Ананьев',	'Мой родной город находится очень далеко от Екатеринбурга, около тысячи километров. За год до экзаменов я и не знал о существовании УрФУ, о нём мне рассказали друзья. Так как я не хотел поступать в Москву, Санкт-Петербург, я очень заинтересовался данным вариантом. Поискал в интернете информацию и сразу понял: "Хочу учиться там!". Возможности, уровень обучения, город. Всё меня устраивало. Сдал экзамены достаточно хорошо и подал документы. К счастью, можно было это сделать дистанционно. При выборе направлений подготовки мой взгляд пал на "Программную инженерию" и "Инфокоммуникационные технологии и системы связи". Конечно, я склонялся к первому варианту, ведь я чётко понимал с чем будет связана деятельность. Но из-за рисков непопадания в первую волну остановился на втором. Какое счастье было, когда я узнал от преподавателей с чем нам предстоит работать... Простым языком мы будем программистами, связистами и инженерами. Все эти составляющие мне очень нравятся.',	'Инфокоммуникационные технологии и системы связи',	NULL,	'2',	'f',	7,	3,	'2022-11-07 16:42:36',	'2022-11-07 16:42:36',	NULL),
(5,	'Анастасия Пиксаева',	'Изначально планировала поступать в университет Иннополис, но отбор я не прошла, и выбор пал на УрФУ, т.к. в городе у меня много знакомых, я его знаю, и здесь есть прекрасные возможности развиваться в IT-сфере. Говоря о программе, ввиду своего проектного прошлого, мне нужна была новая и интересная программа, где я могла бы продолжить заниматься проектами, ИВТ удовлетворило это желание.',	'Информатика и вычислительная техника',	NULL,	'2',	'f',	8,	3,	'2022-11-07 16:44:04',	'2022-11-07 16:44:04',	NULL),
(6,	'Пётр Кузьмин',	'Со школы любил математику и информатику, поэтому направление было понятно сразу. Но на желаемую специальность не прошёл, поэтому можно сказать, что попал на программу «Радиоэлектронные системы» по счастливой случайности.',	'Радиоэлектронные системы',	2008,	NULL,	'f',	9,	3,	'2022-11-07 16:47:24',	'2022-11-07 16:47:24',	NULL);
